mapper.insert:
/*kr.or.hku.classroom.mapper.AssignmentMapper.regi */

		SELECT SEQ_ASSIGNMENT.nextval from dual

	INSERT INTO ASSIGNMENT(
		  asm_no
		, lecap_no
		, asm_week
		, asm_ttl
		, asm_cn
		, asm_pro_nm
		, asm_regdate
		, atch_file_no
	)VALUES(
		#{asmNo}
		, #{lecapNo}
		, #{asmWeek}
		, #{asmTtl}
		, #{asmCn}
		, #{asmProNm}
		, sysdate
		, #{atchFileNo}
	)

/*kr.or.hku.classroom.mapper.AssignmentMapper.giveAssignToStd */
	INSERT INTO ASSIGNMENT_SUBMIT(
		asmsb_no
		, asm_no
		, std_no
		, avl_code
	)VALUES(
		seq_assign_submit.nextval
		, #{asmNo}
		, #{stdNo}
		, 'N'
	)

mapper.update:
	/* kr.or.hku.classroom.mapper.AssignmentMapper.update */
		UPDATE assignment
        SET 
            asm_week = #{asmWeek},
            asm_ttl = #{asmTtl},
            asm_cn = #{asmCn},
            asm_regdate = sysdate,
      		atch_file_no = null
        WHERE asm_no = #{asmNo} 
        AND lecap_no = #{lecapNo}

/* kr.or.hku.classroom.mapper.AssignmentMapper.giveScore */
	UPDATE assignment_submit 
	SET 
		asmsb_scr = #{asmsbScr},
		avl_code = 'Y'
	WHERE std_no = #{stdNo}
	AND asm_no = #{asmNo}
