mapper.select:
/* kr.or.hku.notice.mapper.DepartmentNoticeMapper.selectDeptNoticeList */ SELECT b.* FROM( SELECT a.*, row_number() over (order by a.depnt_no desc) rnum FROM( SELECT depnt_no , dept_cd , depnt_ttl , depnt_wrtr_nm , depnt_regdate , depnt_read_cnt FROM department_notice WHERE 1=1
ORDER BY depnt_no DESC ) a ) b
WHERE b.rnum >= #{startRow} AND b.rnum <= #{endRow}

mapper.update:
 		/* kr.or.hku.notice.mapper.DepartmentNoticeMapper.incrementReadCnt */
 		UPDATE department_notice
 		SET 
			depnt_read_cnt = depnt_read_cnt + 1
		WHERE depnt_no = #{depntNo}

mapper.insert:
 		/* kr.or.hku.notice.mapper.DepartmentNoticeMapper.insertDeptNotice */
 		INSERT INTO department_notice(
		 	  depnt_no
			, dept_cd
			, depnt_ttl
			, depnt_cn
			, depnt_wrtr_nm
			, depnt_regdate
			, depnt_read_cnt
			, atch_file_no
 		)VALUES(
 			  seq_dept_notice.nextval
			, #{deptCd}
			, #{depntTtl}
			, #{depntCn}
			, #{depntWrtrNm}
			, sysdate
			, 0
			, #{atchFileNo}
 		)

mapper.update:
/* kr.or.hku.notice.mapper.DepartmentNoticeMapper.updateDeptNotice */
UPDATE department_notice
SET
	  depnt_ttl = #{depntTtl}
	, depnt_cn = #{depntCn}
	, depnt_regdate = sysdate
	, atch_file_no = #{atchFileNo}
WHERE depnt_no = #{depntNo}
