mapper.insert:

select SEQ_LECTURE_APLY.nextval from dual

INSERT INTO LECTURE_APLY(
	LECAP_NO
	,SUB_NO
	,PRO_NO
	,FLCTS_NO
	,LECAP_YR
	,LECAP_TG_GRADE
	,LECAP_DT
	,LECAP_CPCT
	,LECAP_SEM
	,APRV_STTS_Cd
	,AVL_cd
	)
VALUES(
#{lecapNo}
,#{subNo}
,#{proNo}
,#{flctsNo}
,#{lecapYr}
,#{lecapTgGrade}
,sysdate
,#{lecapCpct}
,#{lecapSem}
,'wait'
,#{avlCd1}
)

mapper.update:
UPDATE LECTURE_APLY
SET APRV_STTS_CD = 'CANCLE'
	WHERE LECAP_NO = #{LECAPNO}
